import React, { useState, useEffect } from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import FileUploadScreen from './screens/FileUploadScreen';
import Navbar from './screens/Navbar';
import { getSingleFiles } from './data/api';


function App() {
  const [singleFiles, setSingleFiles] = useState([]);
  // const [multipleFiles, setMultipleFiles] = useState([]);

  const getSingleFileslist = async () => {
    try {
      const fileslist = await getSingleFiles();
      setSingleFiles(fileslist);
    } catch (error) {
      console.log(error);
    }
  }
  // const getMultipleFilesList = async () => {
  //   try {
  //       const fileslist = await getMultipleFiles();
  //       setMultipleFiles(fileslist);
  //       console.log(multipleFiles);
  //   } catch (error) {
  //     console.log(error);
  //   }
  // }
  useEffect(() => {
    getSingleFileslist();
  }, []);
  return (
    <>
      <Router>
        <Navbar />
        {/* <Alert alert={alert}/> */}
        <div className="container">
          <Switch>
            <Route>
          <div className="container">
            <FileUploadScreen getsingle={() => getSingleFileslist()} />
          </div>
          </Route>
          <div className="container-fluid mt-5">
            <div className="row">
              <div className="col-6">
                <h4 className="text-success font-weight-bold">Single Files List</h4>
                <div className="row">
                  {singleFiles.map((file, index) =>
                    <div className="col-6">
                      <div className="card mb-2 border-0 p-0">
                        <img src={`http://localhost:8080/${file.filePath}`} height="200" className="card-img-top img-responsive" alt="img" />
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
          </Switch>
        </div>
      </Router>
    </>
  );
}

export default App;
